extension.valueString:
define "SDE Sex":
  case
      when Patient.gender = 'male' then Code { code: 'M', system: 'http://hl7.org/fhir/v3/AdministrativeGender', display: 'Male' }
      when Patient.gender = 'female' then Code { code: 'F', system: 'http://hl7.org/fhir/v3/AdministrativeGender', display: 'Female' }
      else null
    end

define "Numerator":
  exists ( [Observation: "Mammography"] Mammogram
                where Mammogram.status in { 'final', 'amended', 'corrected', 'appended' }
                  and ( Global."Normalize Interval" ( Mammogram.effective ) ends 27 months or less on or before 
                    end of "Measurement Period"
                  )
            )

define "SDE Payer":
  [Coverage: type in "Payer"] Payer
        return {
          code: Payer.type,
          period: Payer.period
        }

define "Has Criteria Indicating Frailty":
  exists ( [DeviceRequest: "Frailty Device"] FrailtyDeviceOrder
            where FrailtyDeviceOrder.status in { 'active', 'on-hold', 'completed' }
              and FrailtyDeviceOrder.intent = 'order'
              and Global."Normalize Interval" ( FrailtyDeviceOrder.authoredOn ) during "Measurement Period"
        )
          or exists ( [Observation: "Frailty Device"] FrailtyDeviceApplied
              where FrailtyDeviceApplied.status in { 'final', 'amended', 'corrected' }
                and Global."Normalize Interval" ( FrailtyDeviceApplied.effective ) overlaps "Measurement Period"
          )
       or exists  ( [Condition: "Frailty Diagnosis"] FrailtyDiagnosis
            where Global."Prevalence Period" ( FrailtyDiagnosis ) overlaps "Measurement Period"
        )
          or exists ( [Encounter: "Frailty Encounter"] FrailtyEncounter
              where FrailtyEncounter.status = 'finished'
                and Global."Normalize Interval" ( FrailtyEncounter.period ) overlaps "Measurement Period"
          )
          or exists ( [Observation: "Frailty Symptom"] FrailtySymptom
              where FrailtySymptom.status in { 'preliminary', 'final', 'amended', 'corrected' }
                and Global."Normalize Interval" ( FrailtySymptom.effective ) overlaps "Measurement Period"
          )

define "Max Long Term Care Period Length":
  Max((collapse("Long Term Care Overlapping Periods"
                union "Long Term Care Adjacent Periods"
            ))LTCPeriods
              return duration in days of LTCPeriods
          )

define "Palliative Care in the Measurement Period":
  exists ( [Observation: "Functional Assessment of Chronic Illness Therapy - Palliative Care Questionnaire (FACIT-Pal)"] PalliativeAssessment
              where PalliativeAssessment.status in { 'final', 'amended', 'corrected' }
                and exists ( PalliativeAssessment.category PalliativeAssessmentCategory
                    where "survey" in FHIRHelpers.ToConcept ( PalliativeAssessmentCategory ).codes
                )
                and Global."Normalize Interval" ( PalliativeAssessment.effective ) overlaps "Measurement Period"
          )
            or exists ( [Encounter: "Palliative Care Encounter"] PalliativeEncounter
                where PalliativeEncounter.status = 'finished'
                  and Global."Normalize Interval" ( PalliativeEncounter.period ) overlaps "Measurement Period"
            )
            or exists ( [Procedure: "Palliative Care Intervention"] PalliativeIntervention
                where PalliativeIntervention.status in { 'completed', 'in-progress' }
                  and Global."Normalize Interval" ( PalliativeIntervention.performed ) overlaps "Measurement Period"
            )
